mapper.select:
SELECT *
FROM route
WHERE route_code = #{param1}, username =#{param2}



 SELECT rc as route_code, n as username FROM
	(SELECT count(pc) as countpc, rc, n 
		FROM (SELECT r.route_code as rc, r.username as n, rp.personal_code as pc 
				FROM route r join route_personal rp 
					on r.username = rp.username 
			  		and r.route_code = rp.route_code
			  	WHERE partner_code = #{partner_code} 

		            	and rp.personal_code in

		                    #{item}


	)subtable group by rc, n ) 
subtable2 WHERE countpc = #{personal_code_len} ;

mapper.update:
UPDATE route 
SET routename=#{routename}, 
    partner_code=#{partner_code},
    sday=#{sday},
    eday=#{eday}
WHERE route_code = #{route_code}, username =#{username}
