mapper.select:
SELECT *
FROM route_postscript
WHERE route_code = #{param1}, username =#{param2}

mapper.update:
UPDATE route 
SET routename=#{routename}, 
    partner_code=#{partner_code},
    sday=#{sday},
    eday=#{eday}
WHERE route_code = #{route_code}, username =#{username}

mapper.select:
SELECT *, IF((route_postcript_rownum in (SELECT route_postscript_rownum 
										 FROM route_postscript_like
										 WHERE username = #{username})
			 ),'true','false') as 'route_like'
FROM rout_postscript
WHERE route_postcript_rownum = #{route_postcript_rownum}
