mapper.update:
UPDATE rout_postscript 
SET comment = #{comment}
WHERE route_postscript_rownum = #{route_postscript_rownum}

mapper.select:
SELECT *, IF((route_postcript_rownum in (SELECT route_postscript_rownum 
										 FROM route_postscript_like
										 WHERE username = #{username})
			 ),'true','false') as 'route_like'
FROM rout_postscript
WHERE route_postcript_rownum = #{route_postcript_rownum}
